/**
 * Get optimal draw width for a league
 * @param {string} leagueCode - Internal league code (e.g., 'UK1', 'IT1', 'ES1')
 * @returns {number} - Calibrated draw width for the league
 */
export function getDrawWidth(leagueCode) {
  // If no league code provided, use global default
  if (!leagueCode) {
    return DRAW_WIDTH_CONFIG.global;
  }

  // TIER 1: Try direct lookup first (for calibration system codes like 'premier-league')
  if (DRAW_WIDTH_CONFIG[leagueCode]) {
    return DRAW_WIDTH_CONFIG[leagueCode];
  }

  // TIER 2: Try mapping from internal code
  const mappedCode = LEAGUE_CODE_MAPPING[leagueCode];
  if (!mappedCode) {
    // Unknown league - use global default
    return DRAW_WIDTH_CONFIG.global;
  }

  // TIER 3: Check if mapped code is a calibrated league
  if (DRAW_WIDTH_CONFIG[mappedCode]) {
    return DRAW_WIDTH_CONFIG[mappedCode];
  }

  // TIER 4: Check if mapped code is a regional default
  if (REGIONAL_DEFAULTS[mappedCode]) {
    return REGIONAL_DEFAULTS[mappedCode];
  }

  // TIER 5: Fallback to global default
  return DRAW_WIDTH_CONFIG.global;
}